package unrn.model;

import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

class Agenda {
    private final List<Contacto> contactos = new ArrayList<>();

    Agenda() {
    }

    void agregarContacto(String nombre, List<NumeroTelefono> telefonos) {
        Contacto nuevo = new Contacto(nombre, telefonos, contactos);
        contactos.add(nuevo);
    }

    List<Contacto> listarContactos() {
        return Collections.unmodifiableList(contactos);
    }
}

package unrn.model;

public class Telefono {
    private final String codigoArea;
    private final String numero;

    public Telefono(String codigoArea, String numero) {
        if (codigoArea == null || !codigoArea.matches("\\d{4}")) {
            throw new RuntimeException("El código de área debe tener exactamente 4 dígitos");
        }
        if (numero == null || numero.length() < 6 || numero.length() > 7) {
            throw new RuntimeException("El número debe tener entre 6 y 7 caracteres");
        }
        this.codigoArea = codigoArea;
        this.numero = numero;
    }

    // ...sin getters ni setters...
}
